body.query:
fragment MovieFestivalTinyFragment on MovieFestival {
  id: _id
  name
  startYear
  __typename
}

fragment PersonTinyFragment on Person {
  id: _id
  name
  __typename
}

query movie($id: String!) {
  movie(id: $id) {
    id: _id
    title
    countries
    type
    genres
    duration
    ratings {
      averageRating
      name
      __typename
    }
    releaseYear
    classification
    streamBy {
      name
      yearAdded
      __typename
    }
    produced {
      id {
        ...PersonTinyFragment
        __typename
      }
      __typename
    }
    directed {
      id {
        ...PersonTinyFragment
        __typename
      }
      __typename
    }
    cast {
      id {
        ...PersonTinyFragment
        __typename
      }
      as
      __typename
    }
    written {
      id {
        ...PersonTinyFragment
        __typename
      }
      __typename
    }
    crew {
      id {
        ...PersonTinyFragment
        __typename
      }
      as
      cat
      job
      __typename
    }
    awards {
      category
      festival {
        ...MovieFestivalTinyFragment
        __typename
      }
      won
      year
      __typename
    }
    image {
      poster
      __typename
    }
    boxOffice
    production
    language
    plot
    remakes {
      title
      releaseYear
      directed {
        id {
          ...PersonTinyFragment
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
